S[2] = 2*X - 0.5*Y - 0.5*Z;
S[3] = S[2] + 0.5*cross((a,b,c),S[1] - S[2]);
S[4] = S[1] + S[3] - S[2];

draw(surface(S[1]--S[2]--S[3]--S[4]--cycle),paleyellow,nolight);
